#!/bin/bash
# Single GPU evaluation script for ResNet18 on CIFAR10 with searched bit width config

# Set environment variables for single GPU training
unset WORLD_SIZE
unset RANK
unset LOCAL_RANK
unset MASTER_ADDR
unset MASTER_PORT

# Make sure only one GPU is visible
export CUDA_VISIBLE_DEVICES=0

# Path to the bit width configuration file (saved from search phase)
# This file is automatically generated in the search output directory
BIT_WIDTH_CONFIG="./search/resnet18_cifar10_single_gpu_search_bit_width_config.json"

# Run evaluation
# Note: dataloader.path and resume.path are already configured in the YAML file
# Only bit_width_config_path needs to be passed via command line
python main.py configs/eval/eval_resnet18_cifar10_single_gpu.yaml \
    --bit_width_config_path $BIT_WIDTH_CONFIG

